class TrimmedWire(Wire_):
    """Trim a wire between :attr:`limit1` and :attr:`limit2`.
    These limits can be ``Surfaces``, ``Curves``, ``Points`` or parameters.

    Usage::

    >>> from parapy.geom import (
    ...     Line, Plane, Point, Polygon, TrimmedWire, VX, VY)
    >>> # a straight 2-edge wire with a vertex in the middle
    >>> wire = Polygon((Point(0, 0), Point(1, 0), Point(2, 0)),
    ...                force_closure=False)
    >>> # trim with 2 Points
    >>> trimmed = TrimmedWire(wire, Point(0.5, 0), Point(1.5, 0))
    >>> trimmed.start, trimmed.end
    (Point(0.5, 0.0, 0.0), Point(1.5, 0.0, 0.0))
    >>> # trim with 2 Curves
    >>> tool1 = Line(Point(0.5, 0), VY)
    >>> tool2 = Line(Point(1.5, 0), VY)
    >>> trimmed2 = TrimmedWire(wire, tool1, tool2)
    >>> trimmed2.start, trimmed2.end
    (Point(0.5, 0.0, 0.0), Point(1.5, 0.0, 0.0))
    >>> # trim with 2 Surfaces
    >>> tool3 = Plane(Point(0.5, 0), VX)
    >>> tool4 = Plane(Point(1.5, 0), VX)
    >>> trimmed3 = TrimmedWire(wire, tool3, tool4)
    >>> trimmed3.start, trimmed3.end
    (Point(0.5, 0.0, 0.0), Point(1.5, 0.0, 0.0))
    """

    __initargs__ = ["built_from", "limit1", "limit2"]

    #: The `Wire` to trim
    #: :type: parapy.geom.occ.wire.Wire_
    built_from = Input()

    #: First Shape, Point or parameter to trim the ``Wire`` with. If None is
    #: given, it will use the start of :attr:`built_from`.
    #: :type: parapy.geom.Surface | parapy.geom.Curve | parapy.geom.Point | float
    limit1 = Input(None)

    #: First Shape, Point or parameter to trim the ``Wire`` with. If None is
    #: given, it will use the end of :attr:`built_from`.
    #: :type: parapy.geom.Surface | parapy.geom.Curve | parapy.geom.Point | float
    limit2 = Input(None)

    @Attribute
    def _limit1(self):
        # remove None, convert parameters to points
        limit1 = self.limit1
        if limit1 is None:
            return self.built_from.start
        elif isinstance(limit1, Number):
            return self.built_from.point(limit1)

        return limit1

    @Attribute
    def _limit2(self):
        # remove None, convert parameters to points
        limit2 = self.limit2
        if limit2 is None:
            return self.built_from.end
        elif isinstance(limit2, Number):
            return self.built_from.point(limit2)

        return limit2

    @Attribute
    def trimmed_wire_edges(self):
        """Trim initial wire using tool1 and tool2.

        :rtype: list[parapy.geom.occ.edge.Edge_]
        """
        # ensure the wire is ordered
        wire = self.built_from.reordered
        limit1 = self._limit1
        limit2 = self._limit2

        # Quite sure that in a wire u1 < u2 (per definition?)
        assert wire.u1 < wire.u2, "Please contact ParaPy support"

        # We calculate the intersections so we can see if the operation is
        # valid, and where the intersections occur.
        inters1 = wire.intersections(limit1)
        pt1, supports1 = self._get_single_inters_point_with_supports(
            inters1, "limit1")
        # we cannot use the 'u' from inters since it is relative to the
        # support, not to the wire
        u1 = wire.parameter_at_point(pt1)

        inters2 = wire.intersections(limit2)
        pt2, supports2 = self._get_single_inters_point_with_supports(
            inters2, "limit2")
        # we cannot use the 'u' from inters since it is relative to the
        # support, not to the wire
        u2 = wire.parameter_at_point(pt2)

        if u2 < u1:  # !!! CAUTION !!! we don't flip u1 and u2 (not used)
            # from this point we want that inters1 happens before inters2
            # so we flip everything related if that's not the case
            pt1, pt2 = pt2, pt1
            supports1, supports2 = supports2, supports1

        if supports1.intersection(supports2):
            # Since only 2 intersections occur, and they share (one) support,
            # the end-result will be a single edge
            return self._get_single_edge_trimmed_wire(
                supports1, supports2, pt1, pt2)
        else:  # The intersections occur on different edges of the wire
            return self._get_multi_edge_trimmed_wire(
                wire, supports1, supports2,pt1, pt2)

    def _get_single_inters_point_with_supports(self, intersections, tool_name):
        """Return a single intersection point, potentially on multiple
        supporting shapes (if it is intersecting on an existing non-free
        vertex)
        """
        if not intersections:
            raise RuntimeError(f"{tool_name} did not intersect built_from "
                               f"({self.built_from}). Object {self!r}")

        first_inters = intersections[0]
        pt = first_inters["point"]
        supports = {first_inters["support"]}
        for inters in intersections[1:]:
            # make sure that all intersections are actually the same point.
            # It can have multiple supports of it intersects a shared vertex
            if pt == inters["point"]:
                supports.add(inters["support"])
            else:
                raise RuntimeError(f"{tool_name} did intersect built_from "
                                   f"({self.built_from}) at least twice "
                                   f"({pt} and {inters['point']}). "
                                   f"The limits can only intersect built_from "
                                   f"once! Object {self!r}")

        return pt, supports

    def _get_single_edge_trimmed_wire(self, supports1, supports2, pt1, pt2):
        """The case where only one edge of the wire is relevant to the
        intersection, hence the result will be a single edge.
        """
        common_edge = None
        for edge in supports1:
            if edge in supports2:
                common_edge = edge
                break

        if not common_edge:
            # should not happen, guaranteed by the precondition
            raise RuntimeError(f"Could not find a single-edge solution! "
                               f"This should not happen. Object: {self!r}")

        if len(supports1) == 1 or len(supports2) == 1:
            # the common_edge might not be parameterized in the same direction
            # as the wire (which we used to order pt1 and pt2). If they are
            # not, set sense to False to flip the direction of the result.
            sense = (common_edge.parameter_at_point(pt1) <=
                     common_edge.parameter_at_point(pt2))

            # at least one intersection might split the edge
            # (or unfortunately is an endpoint of the wire)
            return [TrimmedCurve(common_edge, limit1=pt1, limit2=pt2,
                                 sense=sense)]
        else:
            # both intersections appear on the ends of the edge: return the
            # common edge
            return [common_edge]

    def _get_multi_edge_trimmed_wire(self, wire, supports1, supports2,
                                     pt1, pt2):
        """Return the edges of the trimmed wire for the case where we know
        that the split will take place on different edges.
        """
        recording = False  # True when edges need to end up in the result
        wire_edges = []
        for edge in wire.ordered_edges:
            if recording:  # we have seen a shape of supports1
                if edge in supports2:
                    # we found the second support (second intersection)
                    if len(supports2) == 1:
                        if pt2 == edge.end or pt2 == edge.start:
                            # endpoint intersection
                            wire_edges.append(edge)
                            break  # done, all other edges after the split
                        else:  # 'real' split of the edge
                            split = SplitCurve(edge, pt2)
                            e1, e2 = split.edges
                    else:
                        e1, e2 = supports2

                    # we append the first edge (parametrically sorted),
                    # because the other will be after the splitting point
                    if (wire.parameter_at_point(e1.midpoint) <
                            wire.parameter_at_point(e2.midpoint)):
                        wire_edges.append(e1)
                    else:
                        wire_edges.append(e2)

                    break  # done, all other edges after the split
                else:
                    wire_edges.append(edge)
            else:
                if edge in supports1:
                    # we found the first support, which will be in supports1
                    # since we sorted them
                    if len(supports1) == 1:
                        if pt1 == edge.start or pt1 == edge.end:
                            # endpoint intersection
                            e1 = e2 = edge
                        else:  # 'real' split of the edge
                            split = SplitCurve(edge, pt1)
                            e1, e2 = split.edges

                        # we append the second edge (parametrically sorted),
                        # because the other will follow the first split
                        if (wire.parameter_at_point(e1.midpoint) <
                                wire.parameter_at_point(e2.midpoint)):
                            wire_edges.append(e2)
                        else:
                            wire_edges.append(e1)
                    else:  # intersection at shared vertex
                        e1, e2 = supports1

                        # we append the second edge (parametrically sorted),
                        # because the other will follow the first split
                        if (wire.parameter_at_point(e1.midpoint) <
                                wire.parameter_at_point(e2.midpoint)):
                            edge_to_add = e2
                        else:
                            edge_to_add = e1

                        if edge_to_add == edge:
                            wire_edges.append(edge_to_add)
                        else:
                            # we will encounter it later, if we add it now,
                            # we'll get duplicates
                            continue

                    recording = True

        return wire_edges

    @Part(in_tree=False)
    def trimmed_wire(self):
        return Wire(hidden=not self.show_details,
                    curves_in=self.trimmed_wire_edges)

    @Attribute
    def TopoDS_Shape(self):
        """BRep representation of the wire.

        :rtype: OCC.TopoDS.TopoDS_Shape
        """
        return self.trimmed_wire.TopoDS_Shape


if __name__ == '__main__':
    from parapy.geom import Plane, rotate90, XOY, LineSegment
    from parapy.gui import display

    rectangle = Rectangle(2, 1)
    wire = Wire(rectangle.edges[1:])

    # # Example - Plane intersects wire twice, line does not intersect
    tool1 = Plane(reference=rotate90(XOY, 'y'))
    tool2 = LineSegment(Point(0,0,0), Point(3,0,0))

    obj = TrimmedWire(built_from=wire,
                      limit1=tool1,
                      limit2=tool2)
    display(obj)
